mapper.update:
update
	docline
set status = #{status}
where
	doc_no = #{docNo}
and
	approver=#{approver}

update
	docline
set status = #{status}
where
	doc_no = #{docNo}
and
	approver=#{approver}

update
	docline
set status = 'afterview'

	doc_no = #{docNo}
and
	approver != #{approver}
and
	status = 'notdecided'


	and lv < #{lv}

mapper.select:
select doc_no from docline
approver = #{id}
and approver_type in ('enforcer', 'referer')
and approver_type in ('approver', 'agreer')
and approver_type in ('approver', 'agreer') and status = #{status}
intersect select doc_no from docline
status = #{status}
max_authority = 'Y' and status = 'approved'
max_authority = 'Y' and status = 'notdecided'
